<?phpfunction cut_symbol_head_end($word){    $temp_array = cut_word($word);	unset($word);    if($temp_array[0] == '<'){unset($temp_array[0]);}	$i = count($temp_array);	if($temp_array[$i] == '>'){	unset($temp_array[$i]);}	return implode('',$temp_array);}function sing_Tag_cut_symbol_head_end($word){    $temp_array = cut_word($word);	unset($word);    if($temp_array[0] == '<'){unset($temp_array[0]);}		$i = count($temp_array);	if($temp_array[$i] == '>'){		unset($temp_array[$i]);}	if($temp_array[$i-1] == '/'){		unset($temp_array[$i-1]);}	return implode('',$temp_array);}?>